export default function Swiper() {
    function MySwiperComponent() {
        useEffect(() => {
          const swiper = new Swiper(".mySwiper", {
            effect: "coverflow",
            grabCursor: true,
            centeredSlides: true,
            slidesPerView: "auto",
            coverflowEffect: {
              rotate: 15,
              stretch: 0,
              depth: 300,
              modifier: 1,
              slideShadows: true,
            },
            loop: true,
          });
      
          // Cleanup function if needed
          return () => {
            swiper.destroy();
          };
        }, []); // Empty dependency array ensures this effect runs only once on mount
      
        return (
          <div className="mySwiper">
            {/* Add your swiper slides here */}
          </div>
        );
      }
}
