import React, { useEffect } from "react";
import Abovenav from "../../components/abovenav/abovenav";
import { BookData } from "../../util/BookData";
export default function DetailsSection() {
  const id = useParams();

  useEffect(() => {
    let newData = BookData.filter;
  });
  return (
    <section className="pt-32 pb-32">
      <div>
        <div className="flex justify-between">
          <div className="w-3/6	text-center">
            <img
              className="h-52 border border-solid"
              src={BookData.book_image}
              alt="book"
            />
          </div>

          <div className="w-1/2">
            <h2 className="text-4xl">{BookData.book_name}</h2>
            <p className="py-4 px-0">{BookData.author_name}</p>
            <p className="py-4 px-0 leading-7">{BookData.book_description}</p>
            <p>
              <b>Language</b>: {BookData.language}
            </p>
            <p>
              <b>Book Length</b> : {BookData.print_length}
            </p>

            <h3 className="text-2xl	pt-4 text-primary-color-bright">&#8377;</h3>

            <a className="inline-block py-4 px-8 bg-primary-color-bright text-white no-underline mt-8 shadow-md transition duration-200 ease-in border-none font-chivo-mono text-base">
              Add To Cart
            </a>
          </div>
        </div>
      </div>
    </section>
  );
}
